package com.code.java.practise.package1;

import java.util.ArrayList;
import java.util.LinkedList;
import java.util.List;

/**
 * 超时
 */
public class Question76 {
    public static void main(String[] args) {
        long start = System.currentTimeMillis();
        String s = new Question76().minWindow("",
                "");
        System.out.println(s);
        System.out.println(System.currentTimeMillis()-start);
    }

    public String minWindow(String s, String t) {
        List<Character> scharacters = new LinkedList<>();
        for (char schar : s.toCharArray()) {
            scharacters.add(schar);
        }

        int start = 0;
        int end = 0 + t.length();
        int len = Integer.MAX_VALUE;
        String result = "";
        while (start <= end && end <= s.length()) {
            if (end - start > t.length() && isContain(start, end, scharacters, t)) {
                String substring = s.substring(start, end);
                if (substring.length() < len) {
                    result = substring;
                    len = substring.length();
                }
                start++;
            } else {
                end++;
            }
        }
        return result;
    }

    private boolean isContain(int start, int end, List<Character> scharacters, String t) {
        List<Character> dest = new LinkedList<>(scharacters.subList(start, end));
        char[] chars = t.toCharArray();
        for (char aChar : chars) {
            if (!dest.contains(Character.valueOf(aChar))) {
                return false;
            }
            dest.remove(Character.valueOf(aChar));
        }
        return true;
    }
}
